CREATE TABLE level1(
	level1_id INT AUTO_INCREMENT PRIMARY KEY,
	level1_name VARCHAR(50)	
);

CREATE TABLE `product` (
  product_id INT(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
  product_name VARCHAR(255) DEFAULT NULL,
  product_market_price DOUBLE DEFAULT NULL,
  product_store_price DOUBLE DEFAULT NULL,
  product_photo VARCHAR(255) DEFAULT NULL,
  `num` INT(11) DEFAULT NULL,
  product_desc VARCHAR(255) DEFAULT NULL,
  product_ishot INT(11) DEFAULT NULL,
  product_date DATETIME DEFAULT NULL,
  `csid` INT(11) DEFAULT NULL);
  


